while True:    print("\nplease input two numbers")    print("input 'q' to quit")    try:        a = input("The first number : ")        if a == "q":            break        a = int(a)    except ValueError:        print("Input error, it should be a number")    else:        while True:            try:                b = input("The second number : ")                if b =="q":                    break                b = int(b)            except ValueError:                print("Input error, it should be a number")            else:                c = a+b                print("{} is the sum of {} and {}".format(c,a,b))                break        if b=="q":            break